var feeds = new Set();

(function(document) {

/**
 * Http request to fbserve.herokuapp.com.
 *
 * @param the text or url to send to the server.
 */

function httpGet(input, type, data) {

    var server = "https://trustfb.herokuapp.com/";
    var contents = "?content=";
    var page; var ab; var cb; var ab = ""; 
    if(type=="url") page = decode(input); 
    else page = input; 
    if(type=="url") ab = ab + " http: URL , Origin ????^^^^^^^"; else ab = ab + "Personal Opinion"; 
    var cb = ab;
    //console.log(page);
    var theUrl = server+contents+page;
    theUrl = theUrl.replace("&", "^");

                var div = document.createElement('div'),
                button = Ladda.create(div);
                data.appendChild(div); 
                div.innerHTML = cb; 
                if(cb=="Personal Opinion") div.style = "front-weight:bold; padding: 1px; position:absolute; bottom: 9px; right: 50px;background: #F45E48; font-size: 13px; color: #FEFEFE;";
                else div.style = "front-weight:bold; padding: 1px; position:absolute; bottom: 9px; right: 50px;background: #FCAE05; font-size: 13px; color: #FEFEFE;";

    fetch(theUrl)
        .then(function(res)
        { return res.text(); })
        .then(function(text)
        {
                var div = document.createElement('div'),
                    button = Ladda.create(div);
                data.appendChild(div);

                if(text=="verified") div.innerHTML = "Verified OK.";
                else div.innerHTML = "Not Verified";

                if(text=="verified") div.style = "front-weight:bold; padding: 2px; position:absolute; top: 3px; right: 30px;background: #3b5998; font-size: 14px; color: #FEFEFE;";
                else div.style = "front-weight:bold; padding: 2px; position:absolute; top: 3px; right: 30px;background: #EE6C24; font-size: 14px; color: #FEFEFE;";
        });
}

/*
 * Parse through Facebook's encoded url for the actual url
 *
 */
function decode(code) {

    var res = "" + code;
    res = res
        .replace("http://l.facebook.com/l.php?u=", "")
        .replace(/%3A/gi, ":")
        .replace(/%F/gi, "/")
        .replace(/%2F/gi, "/");

    var end = res.substr(res.indexOf("^h"), res.length);
    res = res.replace(end, "");
    var end2 = res.substr(res.indexOf("&"), res.length);
    res = res.replace(end2, "");

    console.log(res);

    return res;
}  
function decodey(code) {

    var res = "" + code;
    res = res
        .replace("http://l.facebook.com/l.php?u=", "")
        .replace(/%3A/gi, ":")
        .replace(/%F/gi, "/")
        .replace(/%2F/gi, "/");

    var end = res.substr(res.indexOf("^h"), res.length);
    res = res.replace(end, "");
    var end2 = res.substr(res.indexOf("&"), res.length);
    res = res.replace(end2, "");
    res = res.substr(7,35);
    console.log(res);

    return res;
}  

/**
 * Receive each Facebook post and analyze texts, urls, pics for validity.
 * Refreshes every second.
 *
 */
setInterval(function() {

    var test = document.getElementsByClassName('_4-u2 mbm _5v3q _4-u8');

    for(var i=0; i<test.length; i++) {

        var data = test[i];

        // Check if feed needs to be modified

        if(!feeds.has(data)) {
            feeds.add(data);

            // Send server requests

            var statement = "";

            var processed = false;

            var linked = test[i].querySelector('._6ks');
            if(!processed && linked != null && linked.querySelector('a')!=undefined) {
                //console.log(linked.querySelector('a'));
                httpGet(linked.querySelector('a').href, "url", data);
                processed = false;
var de;
var de = decodey(linked.querySelector('a').href); 
var div = document.createElement('div'),
                button = Ladda.create(div);
                data.appendChild(div); 
                div.innerHTML = "Source:" + de; 
                div.style = "front-weight:bold; padding: 1px; position:absolute; bottom: 73px; left: 15px;background: #8924EE; font-size: 15px; color: #FEFEFE;";

            }


            var link = test[i].querySelector('._5pbx.userContent');
            if(!processed && link != null && link.querySelector('a') != null && link.querySelector('a').href!=undefined) {
                //console.log(link);
                httpGet(link.href, "url", data);
            }

            var picComment = test[i].querySelector('.uiScaledImageContainer._4-ep');
            if(picComment != null && picComment.src!=undefined) {
                //console.log(picComment);
                httpGet(picComment.src, "image", data);
                processed = false;
            }

            var picPost = test[i].querySelector('._46-h._517g');
            if(!processed && picPost != null && picPost.src!=undefined) {
                //console.log(picPost);
                httpGet(picPost.src, "image", data);
                processed = false;
            }

            var text = test[i].querySelector('._5pbx.userContent');
            if(!processed && text != null && text.textContent!=undefined) {
                //console.log(text);
                httpGet(text.textContent, "text", data);
                processed = false;
            }
        } else {
            //console.log("have feed");
        }
    }

}, 1000);

})(document);
