def batch_rename(work_dir,old_part,new_part):
    for filename in os.listdir(work_dir):
        if filename.find(old_part) != -1:
            print filename
            newname = filename.replace(old_part,new_part)
            os.rename(
                os.path.join(work_dir,filename),
                os.path.join(work_dir,newname)
                )
